"use strict";window.addEventListener("load",function(){headerShow()});var siteTitle=document.querySelector(".header-title");function headerShow(){gsap.to(siteTitle,3,{y:0,delay:1,ease:Power4.easeOut,opacity:1})}var headerMenu=document.querySelectorAll(".js-hover");headerMenu.forEach(function(e){e.addEventListener("mouseover",function(){gsap.to(e,{color:"#87cefa",duration:.5,ease:Power4.easeIn,onStart:function(){e.innerHTML="click"}})}),e.addEventListener("mouseleave",function(){gsap.to(e,{color:"#df5656",duration:.8,ease:Power4.easeIn,onStart:function(){e.innerHTML="reverse"}})}),console.log(e)});